//// DD_Menu.r//#define menu_Apple		128#define menu_File		129#define menu_Edit		130#define menu_Item		131resource 'MBAR' (128) {	{		menu_Apple,		menu_File,		menu_Edit,		menu_Item	}};resource 'MENU' (menu_Apple, "Apple") {	menu_Apple, textMenuProc, allEnabled, enabled,	apple,	{		"About Drag & Drop Demo…", noIcon, noKey, noMark, plain	}};resource 'Mcmd' (menu_Apple, "Apple") { {	cmd_About} };resource 'MENU' (menu_File, "File") {	menu_File, textMenuProc, allEnabled, enabled,	"File",	{		"New Demo Window",			noIcon, "N",	noMark, plain,		"Close Demo Window",		noIcon, "W",	noMark, plain,		"-",						noIcon, noKey,	noMark, plain,		"Play Droplet on Drop",		noIcon,	noKey,	noMark,	plain,		"Play WildEep on Enter",	noIcon, noKey,	noMark,	plain,		"Play Sosumi on Exit",		noIcon, noKey,	noMark, plain,		"-",						noIcon, noKey,	noMark, plain,		"Use LDragTask",			noIcon,	noKey,	noMark,	plain,		"Use CDragItemTask",		noIcon, noKey,	noMark, plain,		"-",						noIcon, noKey,	noMark, plain,		"Quit",						noIcon, "Q",	noMark, plain,	}};resource 'Mcmd' (menu_File, "File") { {	cmd_New,				// New Demo Window      N	cmd_Close,				// Close Demo Window    W	msg_Nothing,			// -----------------------	cmd_FlipDropSound,		// Play Droplet on Drop	cmd_FlipEnterSound,		// Play WildEep on Enter	cmd_FlipExitSound,		// Play Sosumi on Exit	msg_Nothing,			// -----------------------	cmd_LDragTask,			// Use LDragTask	cmd_CDragItemTask,		// Use CDragItemTask	msg_Nothing,			// -----------------------	cmd_Quit,				// Quit                 Q} };resource 'MENU' (menu_Edit, "Edit") {	menu_Edit, textMenuProc, allEnabled, enabled, "Edit",	{		"Undo",			noIcon, "Z",	noMark, plain,		"-",			noIcon, noKey,	noMark, plain,		"Cut",			noIcon, "X",	noMark, plain,		"Copy",			noIcon, "C",	noMark, plain,		"Paste",		noIcon, "V",	noMark, plain,		"Clear",		noIcon, "B",	noMark, plain,	}};resource 'Mcmd' (menu_Edit, "Edit") { {	cmd_Undo,				// Undo            Z	msg_Nothing,			// ------------------	cmd_Cut,				// Cut             X	cmd_Copy,				// Copy            C	cmd_Paste,				// Paste           V	cmd_Clear,				// Clear           B} };resource 'MENU' (menu_Item, "Item") {	menu_Item, textMenuProc, allEnabled, enabled,	"Item",	{		"Rectangle",	noIcon, noKey, noMark, plain,		"Oval",			noIcon, noKey, noMark, plain,		"-",			noIcon, noKey, noMark, plain,		"White",		noIcon, noKey, noMark, plain,		"Light Gray",	noIcon, noKey, noMark, plain,		"Gray",			noIcon, noKey, noMark, plain,		"Dark Gray",	noIcon, noKey, noMark, plain,	}};resource 'Mcmd' (menu_Item, "Item") { {	cmd_Rectangle,			// Rectangle	cmd_Oval,				// Oval	msg_Nothing,			// -----------	cmd_FillWhite,			// White	cmd_FillLtGray,			// Light Gray	cmd_FillGray,			// Gray	cmd_FillDkGray,			// Dark Gray} };